#!/usr/bin/env ruby

def longest_palindrome(s)
  
  max_length = 1
  max_i = 0
  max_j = 0

  (1...(s.size)).each do |j|
    [-1, 0, 1].each do |offset|
      i = j - max_length + offset
      next if i < 0
      if s[i] == s[j] and s[i..j] == s[i..j].reverse
        # puts "Found i, j (#{i}, #{j}), #{s[i..j]}"
        max_i = i
        max_j = j
        max_length = j + 1 - i
        break
      end
    end
    # puts("sub_string(#{s[0..j]}), max_string(#{s[max_i..max_j]}) length(#{max_length})")
  end

  s[max_i..max_j]
  
end


words = []
